import React, {useState} from 'react';

import { 
  Heading, 
  ButtonContainer,
  CarouselItem,
  CarouselCaption
} from './styles';

import { Carousel } from 'react-bootstrap';
import ButtonPrimary from '../Buttons/ButtonPrimary';

import ImageHeaderMalibu from '../../assets/images/header-malibu.jpg';
import ImageHeaderTrucker from '../../assets/images/header-trucker.jpg';

function HeaderCarousel() {
  const [index, setIndex] = useState(0);

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };

  return (
      <Carousel indicators={false} interval={1000} activeIndex={index} onSelect={handleSelect}>
        <CarouselItem>
          <img
            className="d-block w-100"
            src={ImageHeaderMalibu}
            alt="First slide"
          />
          <CarouselCaption>
            <Heading>
                НОВЫЙ АВТОМОБИЛЬ В РАССРОЧКУ
            </Heading>
            <ButtonContainer>
              <ButtonPrimary>Купить новые автомобили</ButtonPrimary>
            </ButtonContainer>
          </CarouselCaption>
        </CarouselItem>
        <CarouselItem>
          <img
            className="d-block w-100"
            src={ImageHeaderTrucker}
            alt="Second slide"
          />
          <CarouselCaption>
            <Heading>
              ПОДЕРЖАННЫЙ АВТОМОБИЛЬ В РАССРОЧКУ
            </Heading>
            <ButtonContainer>
              <ButtonPrimary>Купить Б/У автомобиль</ButtonPrimary>
            </ButtonContainer>
          </CarouselCaption>
        </CarouselItem>
      </Carousel>
  );
};

export default HeaderCarousel;
