import styled from 'styled-components';
import Slider from "react-slick";

export const  Container = styled.div`
  width: 100%;
  height: fit-content;
  background-color: white;
  position: relative;
  text-align: center;
  margin-bottom: 100px;

`;

export const Heading = styled.span`
  font-size: 48px;
  letter-spacing: 0px;
  color: #000000;
  font-weight: 500;
`;

export const Title = styled.h1`
  font-size: 30px;
`;
export const Wrapper = styled.div`
    &:focus {
      outline: none;
    }
    img {
      width: 100%;
      height: 100%;
      object-fit: cover;
    }
  &:hover {
    cursor: grab;
  }
`;

export const SliderStyled = styled(Slider)`
  width: 100vw;
  margin: auto;
  display: flex;
  margin-top: 50px;
`;
