import styled from 'styled-components';


export const Container = styled.div`
  width: 65vw;
  height: 30vh;
  display: flex;
  flex-direction: row;
  box-shadow: 9px 9px 16px #06060616;
  margin-left: auto;
  margin-right: auto;
  margin-top: -70px;
  margin-bottom: 100px;
  position: relative;
    @media screen and ( max-width: 1300px) {
      width: 90vw;
    }
    @media screen and ( max-width: 768px ) {
      width: 80vw;
      height: fit-content;
      display: flex;
      flex-direction: column;
      margin-left: auto;
      margin-right: auto;
    }
`;

export const Card = styled.div`
  width: 25%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  border: 1px solid #DADADA;
  background-color: white;
  text-align: center;
  @media screen and ( max-width: 768px ) {
    width: 100%;
    height: 40vh;
  }
    h1 {
      font-size: 18px;
      line-height: 25px;
      margin-left: 50px;
      margin-right: 50px;
      padding-top: 40px;
    }
    h2 {
      font-size: 18px;
      line-height: 25px;
      margin-left: 50px;
      margin-right: 50px;
      padding-top: 20px;
    }
    h3 {
      font-size: 18px;
      line-height: 25px;
      margin-left: 50px;
      margin-right: 50px;
      padding-top: 20px;
    }
    h4 {
      font-size: 18px;
      line-height: 25px;
      margin-left: 50px;
      margin-right: 50px;
      padding-top: 40px;
    }
    &:hover {
      background-color: #00AF66;
      cursor: pointer;
      span {
        color: white;
      }
    }
`;

export const ImageContainer1 = styled.div`
  width: 20%;
  height: 21%;
  img {
   width: 100%;
   height: 100%;
  }
`;
export const ImageContainer2 = styled.div`
  width: 23%;
  height: 25%;
  // margin-bottom: -50px;
  img {
    width: 100%;
    height: 100%;
  }
`;
export const ImageContainer3 = styled.div`
  width: 22%;
  height: 23%;
  // margin-bottom: -40px;
  img {
    width: 100%;
    height: 100%;
  }
`;
export const ImageContainer4 = styled.div`
  width: 21%;
  height: 23%;
  // margin-bottom: -30px;
  img {
    width: 100%;
    height: 100%;
  }
`;
