import styled from 'styled-components';


export const Container = styled.div`
  width: 100vw;
  height: fit-content;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  background-color: white;
  background-color: #D8D8D85C;
  position: relative;
`;

export const Heading = styled.h1`
  font-size: 48px;
  font-weight: 600;
  line-height: 55px;
  color: #4D4D4D;
  margin-top: 100px;
  margin-right: 50px;
  margin-left: 50px;
  @media screen and (max-width: 600px) {
    font-size: 28px;
    line-height: 35px;
    margin-left: 0;
    margin-right: 0;
  }
  @media screen and (max-width: 460px) {
    font-size: 25px;
    line-height: 30px;
  }
`;

export const Row = styled.div`
  width: 100vw;
  height: fit-content;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
  justify-content: space-evenly;
  margin-top: 50px;
  margin-bottom: 50px;
`;

export const BtnWrapper = styled.div`
  width: 100%;
  margin-left: auo;
  margin-right: auto;
  margin-bottom: 50px;
`;
